#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {

    int N,M;
    int R;

    cin >> N >> M >> R;

    vector<vector<int>> matrix(N+1, vector<int>(M+1));
    for(int i=1 ; i <= N; i++) {
        for (int j=1 ; j <= M; ++j) {                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
            cin >> matrix[i][j];
        }
    }

    while(R--) {
        int r1,c1,r2,c2;
        cin >> r1 >> c1 >> r2 >> c2;
        //limit the boundaries

        if(r1 > r2 || c1 > c2 || (r1 == r2 && r1 > N) || (c1 == c2 && c1 > M)) {
            cout << "INVALID\n";
            continue;
        } else if (r1 > N || c1 > M || r2 <= 0 || c2 <= 0) {
            cout << "OUTSIDE\n";
            continue;
        }

        r1 = min(r1,N); r2 = min(r2,N); c1 = min(c1,M); c2 = min(c2,M);
        //fixing number to corespond to the matrix index

        int maxx = matrix[r1][c1];
        for (int i=r1; i <= r2; ++i) {
            for(int j=c1; j <= c2; ++j) {
                maxx = max(maxx, matrix[i][j]);
            }
        }

        cout << maxx << "\n";
    }



    return 0;
}